import React, { useState } from "react";
import { Header } from "../MutualComponents/Header/Header";
import { CartItem } from "./components/CartItem/CartItem";
import OrderSummary from "./components/OrderSummary/OrderSummary";
import NewsletterSection from "../MutualComponents/Newsletter/Newsletter";
import { Footer } from "../MutualComponents/Footer/Footer";
import Checkout from "../Checkout/Checkout";
import styles from "./Cart.module.css";

const initialCartItems = [
  {
    id: 1,
    image: "https://via.placeholder.com/150",
    title: "Gradient Graphic T-shirt",
    size: "Large",
    color: "White",
    price: 145,
    quantity: 1,
  },
  {
    id: 2,
    image: "https://via.placeholder.com/150",
    title: "Checkered Shirt",
    size: "Medium",
    color: "Red",
    price: 180,
    quantity: 1,
  },
  {
    id: 3,
    image: "https://via.placeholder.com/150",
    title: "Skinny Fit Jeans",
    size: "Large",
    color: "Blue",
    price: 240,
    quantity: 1,
  },
];

export const Cart = () => {
  const [cartItems, setCartItems] = useState(initialCartItems);
  const [isCheckout, setIsCheckout] = useState(false);

  const updateQuantity = (id, newQuantity) => {
    setCartItems(
      cartItems.map((item) =>
        item.id === id ? { ...item, quantity: newQuantity } : item
      )
    );
  };

  const removeItem = (id) => {
    setCartItems(cartItems.filter((item) => item.id !== id));
  };

  const subtotal = cartItems.reduce(
    (acc, item) => acc + item.price * item.quantity,
    0
  );
  const discount = subtotal * 0.2;
  const deliveryFee = 15;
  const total = subtotal - discount + deliveryFee;

  return (
    <main className={styles.cart}>
      <Header />

      <div className={styles.container}>
        <div className={styles.divider}></div>

        <h1 className={styles.title}>
          {isCheckout ? "Payment Methods" : "Your Cart"}
        </h1>

        <div className={styles.content}>
          <section className={styles.cartItems}>
            {!isCheckout &&
              cartItems.map((item) => (
                <CartItem
                  key={item.id}
                  {...item}
                  onQuantityChange={(newQuantity) =>
                    updateQuantity(item.id, newQuantity)
                  }
                  onRemove={() => removeItem(item.id)}
                />
              ))}
          </section>

          <aside className={styles.sidebar}>
            <OrderSummary
              subtotal={subtotal}
              discount={discount}
              deliveryFee={deliveryFee}
              total={total}
              isCheckout={isCheckout}
              onAction={() => {
                if (isCheckout) {
                  alert("Payment processed successfully!");
                } else {
                  setIsCheckout(true);
                }
              }}
            />
          </aside>
        </div>

        <NewsletterSection />
      </div>
      <Footer />
    </main>
  );
};
