// Checkout.jsx
import React, { useState } from "react";
import styles from "./Checkout.module.css";

const Checkout = ({ onPay, total }) => {
  const [paymentMethod, setPaymentMethod] = useState(null);

  const handlePay = () => {
    if (paymentMethod === "direct") {
      alert("Payment successful through VNPay!");
      onPay();
    } else if (paymentMethod === "store") {
      alert("Payment selected as Store Payment. Please proceed to the store.");
      onPay();
    } else {
      alert("Please select a payment method.");
    }
  };

  const paymentMethods = [
    { method: "direct", label: "Thanh toán trực tiếp (VNPay)" },
    { method: "store", label: "Thanh toán tại cửa hàng" }
  ];

  // Link VNPay API or QR Code for VNPay Payment
  const vnPayQRCode = `https://vnpay.vn/api/qrcode/${total}`;

  return (
    <div className={styles.checkout}>
      <h3>Chọn phương thức thanh toán</h3>
      <div className={styles.paymentOptions}>
        {paymentMethods.map((method) => (
          <div key={method.method} className={styles.option}>
            <input
              type="radio"
              id={method.method}
              name="paymentMethod"
              value={method.method}
              onChange={() => setPaymentMethod(method.method)}
            />
            <label htmlFor={method.method}>{method.label}</label>
          </div>
        ))}
      </div>

      {paymentMethod === "direct" && (
        <div className={styles.qrContainer}>
          <h4>Thanh toán qua VNPay</h4>
          <img
            src={vnPayQRCode}
            alt="VNPay QR Code"
            className={styles.qrCode}
          />
          <p>Quét mã QR để thanh toán số tiền ${total.toFixed(2)}</p>
        </div>
      )}

      <button className={styles.payButton} onClick={handlePay}>
        Thanh toán
      </button>
    </div>
  );
};

export default Checkout;
