import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./NewProduct.module.css";

const NewProduct = () => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Simulating fetching new products from an API
    const fetchProducts = async () => {
      try {
        // Replace the below mock data with your API call if available
        const response = await fetch("https://api.example.com/new-products"); // Example API endpoint
        const data = await response.json();

        // Sort by rating (highest first) and get top 4 products
        const topProducts = data.products
          .sort((a, b) => b.rating - a.rating)
          .slice(0, 4);
        setProducts(topProducts);
      } catch (error) {
        console.error("Error fetching products:", error);

        // Fallback mock data if API call fails
        const mockData = [
          {
            id: 1,
            name: "Fuji XT-5",
            category: "Camera",
            price: 25,
            discountedPrice: 20,
            rating: 4.0,
            image:
              "https://cdn.builder.io/api/v1/image/assets/TEMP/061aeef4929baa7311c51b60c045d1356c7bef644be41c6efa6f3b6d6d9a6d77?placeholderIfAbsent=true&apiKey=0a3e8fdc78024414a2749a36ad80ee2c",
          },
          {
            id: 2,
            name: "Sony ULT Tower 10",
            category: "Speaker",
            price: 75,
            discountedPrice: 75,
            rating: 3.5,
            image:
              "https://cdn.builder.io/api/v1/image/assets/TEMP/620a1c99cd138f4279f733b22ef7213cefe4fbaaa673ff9c51ca006378723f78?placeholderIfAbsent=true&apiKey=0a3e8fdc78024414a2749a36ad80ee2c0",
          },
          {
            id: 3,
            name: "Samsung Z Flip 6",
            category: "Smartphone",
            price: 25,
            discountedPrice: 15,
            rating: 4.5,
            image:
              "https://cdn.builder.io/api/v1/image/assets/TEMP/8bc234606f19c11a185c160124b803006edebb82dc54c64a6a37021a7b9866bc?placeholderIfAbsent=true&apiKey=0a3e8fdc78024414a2749a36ad80ee2c",
          },
          {
            id: 4,
            name: "Apple iPhone 16 Pro Max",
            category: "Smartphone",
            price: 25,
            discountedPrice: 15,
            rating: 5.0,
            image:
              "https://cdn.builder.io/api/v1/image/assets/TEMP/d07bc4911a6420610641cd1d5c59b0319418027d239c31f5aa62a2bdb2e96ec1?placeholderIfAbsent=true&apiKey=aa0c3b8d094f45b48d52977318229ea8",
          },
        ];

        const topProducts = mockData
          .sort((a, b) => b.rating - a.rating)
          .slice(0, 4);
        setProducts(topProducts);
      }
    };

    fetchProducts();
  }, []);

  const handleProductClick = (product) => {
    navigate(`/product-info`);
  };

  return (
    <div className={styles.container}>
      <div className={styles.title}>
        <div className={styles.bold}>NEW </div> Products
      </div>
      <div className={styles.productContainer}>
        {products.map((product) => (
          <div
            key={product.id}
            className={styles.productFrame}
            onClick={() => handleProductClick(product)}
          >
            <img
              src={product.image}
              alt={product.name}
              className={styles.productImage}
            />
            <div className={styles.productInfoOverlay}>
              <h3>{product.name}</h3>
              <p>{product.category}</p>
              <p>
                From{" "}
                <span className={styles.originalPrice}>${product.price}</span>
              </p>
              {product.discountedPrice < product.price && (
                <p>
                  <span className={styles.discountedPrice}>
                    ${product.discountedPrice}
                  </span>
                </p>
              )}
              <p>Rating: {product.rating} / 5</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default NewProduct;
