import React, { useState } from 'react';
import styles from './MP.module.css';

const ManageProduct = () => {
  const [products, setProducts] = useState([
    { id: 1, name: 'Product 1', type: 'Type A', price: 100, quantity: 10, status: 'Available', rented: 'Yes', note: 'Popular item' },
    { id: 2, name: 'Product 2', type: 'Type B', price: 200, quantity: 5, status: 'Out of Stock', rented: 'No', note: 'Seasonal' },
    // Add more initial products if needed
  ]);

  const [isEditMode, setIsEditMode] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [filterField, setFilterField] = useState('');
  const [filterValue, setFilterValue] = useState('');

  const toggleEditMode = () => setIsEditMode(!isEditMode);
  const toggleDeleteMode = () => setIsDeleteMode(!isDeleteMode);

  const handleInputChange = (e, index, field) => {
    const updatedProducts = [...products];
    updatedProducts[index][field] = e.target.value;
    setProducts(updatedProducts);
  };

  const handleFilterFieldChange = (e) => {
    setFilterField(e.target.value);
    setFilterValue('');
  };

  const handleFilterValueChange = (e) => {
    setFilterValue(e.target.value);
  };

  // Filtered products based on selected field and value
  const filteredProducts = products.filter(product => {
    if (!filterField || !filterValue) return true;
    return String(product[filterField]).toLowerCase() === filterValue.toLowerCase();
  });

  // Get unique values for the selected filter field
  const getUniqueFilterValues = (field) => {
    const values = products.map(product => product[field]);
    return [...new Set(values)];
  };

  return (
    <div className={styles.manageContainer}>
      <div className={styles.buttonContainer}>
        <select value={filterField} onChange={handleFilterFieldChange} className={styles.filterSelect}>
          <option value="">Filter by</option>
          <option value="id">ID</option>
          <option value="name">Product Name</option>
          <option value="type">Type</option>
          <option value="price">Price</option>
          <option value="status">Status</option>
          <option value="quantity">Quantity</option>
          <option value="rented">Rented</option>
          <option value="note">Note</option>
        </select>
        <select value={filterValue} onChange={handleFilterValueChange} className={styles.filterSelect} disabled={!filterField}>
          <option value="">Select Value</option>
          {filterField && getUniqueFilterValues(filterField).map((value, index) => (
            <option key={index} value={value}>{value}</option>
          ))}
        </select>
        <button onClick={toggleEditMode} className={styles.editButton}>Edit</button>
        <button onClick={toggleDeleteMode} className={styles.deleteButton}>Delete</button>
      </div>

      <table className={styles.table}>
        <thead>
          <tr>
            {isDeleteMode && <th>Select</th>}
            <th>ID</th>
            <th>Product Name</th>
            <th>Type</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Status</th>
            <th>Rented</th>
            <th>Note</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map((product, index) => (
            <tr key={product.id}>
              {isDeleteMode && (
                <td>
                  <input
                    type="checkbox"
                    onChange={() => handleSelectProduct(product.id)}
                  />
                </td>
              )}
              <td>{product.id}</td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.name}
                    onChange={(e) => handleInputChange(e, index, 'name')}
                    className={styles.inputField}
                  />
                ) : (
                  product.name
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.type}
                    onChange={(e) => handleInputChange(e, index, 'type')}
                    className={styles.inputField}
                  />
                ) : (
                  product.type
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="number"
                    value={product.price}
                    onChange={(e) => handleInputChange(e, index, 'price')}
                    className={styles.inputField}
                  />
                ) : (
                  product.price
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="number"
                    value={product.quantity}
                    onChange={(e) => handleInputChange(e, index, 'quantity')}
                    className={styles.inputField}
                  />
                ) : (
                  product.quantity
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.status}
                    onChange={(e) => handleInputChange(e, index, 'status')}
                    className={styles.inputField}
                  />
                ) : (
                  product.status
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.rented}
                    onChange={(e) => handleInputChange(e, index, 'rented')}
                    className={styles.inputField}
                  />
                ) : (
                  product.rented
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.note}
                    onChange={(e) => handleInputChange(e, index, 'note')}
                    className={styles.inputField}
                  />
                ) : (
                  product.note
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageProduct;
