import React, { useState } from 'react';

const FilterSidebar = ({ setFilterCriteria, applyFilter }) => {
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [priceRange, setPriceRange] = useState([0, 300]);
    const [openSubcategories, setOpenSubcategories] = useState({
        'Máy ảnh & Máy quay': false,
        'Ống kính': false,
        'Chân máy': false,
        'Flycam': false,
        'Smart phone': false,
    });
    const [selectedSubcategories, setSelectedSubcategories] = useState([]);

    const handleCategoryToggle = (category) => {
        setOpenSubcategories((prevState) => ({
            ...prevState,
            [category]: !prevState[category],
        }));
    };

    const handleSubcategorySelect = (subcategory) => {
        setSelectedSubcategories((prev) =>
            prev.includes(subcategory)
                ? prev.filter((item) => item !== subcategory)
                : [...prev, subcategory]
        );
    };

    const handlePriceChange = (event, index) => {
        const newPriceRange = [...priceRange];
        newPriceRange[index] = Number(event.target.value);
        setPriceRange(newPriceRange);
    };

    const handleApplyFilter = () => {
        setFilterCriteria({ category: selectedCategory, subcategories: selectedSubcategories, priceRange });
        applyFilter();
    };

    const categories = {
        'Máy ảnh & Máy quay': ['Sony', 'Fujifilm', 'Nikon'],
        'Ống kính': ['Canon', 'Nikon', 'Sigma'],
        'Chân máy': ['Manfrotto', 'Benro'],
        'Flycam': ['DJI', 'Autel'],
        'Smart phone': ['Apple', 'Samsung'],
    };

    return (
        <div style={styles.sidebar}>
            <h3 style={styles.title}>Filters</h3>

            {Object.keys(categories).map((category) => (
                <div key={category} style={styles.section}>
                    <div style={styles.categoryHeader} onClick={() => handleCategoryToggle(category)}>
                        {category}
                        <span style={styles.arrow}>{openSubcategories[category] ? '▴' : '▾'}</span>
                    </div>
                    {openSubcategories[category] && (
                        <ul style={styles.subcategoryList}>
                            {categories[category].map((subcategory) => (
                                <li
                                    key={subcategory}
                                    style={{
                                        ...styles.subcategoryItem,
                                        backgroundColor: selectedSubcategories.includes(subcategory) ? '#FFB13A' : 'transparent',
                                    }}
                                    onClick={() => handleSubcategorySelect(subcategory)}
                                >
                                    {subcategory}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
            ))}

            <div style={styles.section}>
                <h4 style={styles.categoryTitle}>Price</h4>
                <div style={styles.sliderContainer}>
                    <input
                        type="range"
                        min="0"
                        max="300"
                        value={priceRange[0]}
                        onChange={(e) => handlePriceChange(e, 0)}
                        style={styles.slider}
                    />
                    <input
                        type="range"
                        min="0"
                        max="300"
                        value={priceRange[1]}
                        onChange={(e) => handlePriceChange(e, 1)}
                        style={styles.slider}
                    />
                </div>
                <div style={styles.priceRange}>
                    <span>${priceRange[0]}</span>
                    <span>${priceRange[1]}</span>
                </div>
            </div>

            <button onClick={handleApplyFilter} style={styles.applyButton}>Apply Filter</button>
        </div>
    );
};

const styles = {
    sidebar: {
        fontFamily: 'Lato, sans-serif', 
    },
    title: {
        fontSize: '18px',
        fontWeight: 'bold',
        marginBottom: '16px',
    },
    section: {
        marginBottom: '16px',
    },
    categoryHeader: {
        display: 'flex',
        justifyContent: 'space-between',
        cursor: 'pointer',
        fontSize: '16px',
        fontWeight: 'bold',
        padding: '8px 0',
    },
    arrow: {
        fontSize: '12px',
    },
    subcategoryList: {
        listStyleType: 'none',
        padding: '8px 0 0 16px',
        margin: 0,
    },
    subcategoryItem: {
        padding: '4px 0',
        cursor: 'pointer',
        fontSize: '14px',
    },
    sliderContainer: {
        display: 'flex',
        flexDirection: 'column',
        gap: '8px',
        marginBottom: '8px',
    },
    slider: {
        appearance: 'none',
        width: '100%',
        height: '5px',
        background: '#ddd',
        outline: 'none',
        opacity: '0.7',
        transition: 'opacity .15s ease-in-out',
    },
    priceRange: {
        display: 'flex',
        justifyContent: 'space-between',
        fontSize: '14px',
    },
    applyButton: {
        width: '100%',
        padding: '10px 0',
        backgroundColor: 'black',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        fontSize: '16px',
        cursor: 'pointer',
    },
};

export default FilterSidebar;
