import React, { useState } from "react";
import styles from "./Checkout.module.css";

export const Checkout = ({ onPay }) => {
  const [form, setForm] = useState({
    cardNumber: "",
    validDate: "",
    cvv: "",
    cardHolder: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({ ...prevForm, [name]: value }));
  };

  const handlePay = () => {
    // Add validation or API logic here
    if (Object.values(form).some((value) => value === "")) {
      alert("Please fill in all fields.");
      return;
    }
    alert("Payment successful!");
    if (onPay) onPay();
  };

  return (
    <div className={styles.checkout}>
      <div className={styles.paymentMethods}>
        <img
          src="https://via.placeholder.com/50?text=VISA"
          alt="Visa"
          className={styles.paymentIcon}
        />
        <img
          src="https://via.placeholder.com/50?text=MasterCard"
          alt="MasterCard"
          className={styles.paymentIcon}
        />
        <img
          src="https://via.placeholder.com/50?text=PayPal"
          alt="PayPal"
          className={styles.paymentIcon}
        />
        <img
          src="https://via.placeholder.com/50?text=GPay"
          alt="Google Pay"
          className={styles.paymentIcon}
        />
      </div>
      <h2 className={styles.title}>Enter your Card:</h2>
      <form className={styles.form}>
        <label className={styles.label}>
          Card Number:
          <input
            type="text"
            name="cardNumber"
            value={form.cardNumber}
            onChange={handleChange}
            className={styles.input}
            placeholder="XXXX XXXX XXXX XXXX"
            maxLength="19"
          />
        </label>
        <label className={styles.label}>
          Valid Date:
          <input
            type="text"
            name="validDate"
            value={form.validDate}
            onChange={handleChange}
            className={styles.input}
            placeholder="MM/YY"
            maxLength="5"
          />
        </label>
        <label className={styles.label}>
          CVV:
          <input
            type="text"
            name="cvv"
            value={form.cvv}
            onChange={handleChange}
            className={styles.input}
            placeholder="XXX"
            maxLength="3"
          />
        </label>
        <label className={styles.label}>
          Name on Card:
          <input
            type="text"
            name="cardHolder"
            value={form.cardHolder}
            onChange={handleChange}
            className={styles.input}
            placeholder="Enter your Name"
          />
        </label>
        <button type="button" onClick={handlePay} className={styles.payButton}>
          Pay
        </button>
      </form>
    </div>
  );
};

export default Checkout;
