import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../api";
import styles from "./NewProduct.module.css";

const NewProduct = () => {
    const [products, setProducts] = useState([]); // Lưu danh sách sản phẩm
    const [loading, setLoading] = useState(true); // Trạng thái loading
    const [error, setError] = useState(false); // Trạng thái lỗi
    const navigate = useNavigate();

    // Hàm lấy dữ liệu sản phẩm mới
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true); // Bắt đầu trạng thái tải
                setError(false); // Xóa lỗi trước khi lấy dữ liệu mới
                const response = await api.get("/products?sort=new");
                setProducts(response.data || []); // Lưu dữ liệu vào state
            } catch (err) {
                console.error("Failed to fetch new products:", err);
                setError(true); // Cập nhật trạng thái lỗi khi có vấn đề
            } finally {
                setLoading(false); // Đổi trạng thái tải xong
            }
        };

        fetchProducts();
    }, []);

    const handleProductClick = (product) => {
        navigate(`/product-info/${product.id}`, { state: { product } });
    };

    // Hiển thị giao diện khi đang tải dữ liệu hoặc có lỗi
    if (loading) {
        return <div className={styles.loading}>Loading new products...</div>;
    }

    if (error) {
        return <div className={styles.error}>Failed to load products. Please try again later.</div>;
    }

    return (
        <div className={styles.container}>
            <div className={styles.title}>
                <div className={styles.bold}>NEW</div> Products
            </div>
            <div className={styles.productContainer}>
                {products.length > 0 ? (
                    products.map((product) => (
                        <div
                            key={product.id}
                            className={styles.productFrame}
                            onClick={() => handleProductClick(product)}
                        >
                            <img
                                src={product.image || "/path/to/placeholder.jpg"} // Thêm ảnh mặc định khi không có ảnh sản phẩm
                                alt={product.name}
                                className={styles.productImage}
                            />
                            <div className={styles.productInfoOverlay}>
                                <h3>{product.name}</h3>
                                <p>{product.category}</p>
                                <p>
                                    From{" "}
                                    <span className={styles.originalPrice}>${product.price}</span>
                                </p>
                                {product.discountedPrice < product.price && (
                                    <p>
                                        <span className={styles.discountedPrice}>
                                            ${product.discountedPrice}
                                        </span>
                                    </p>
                                )}
                                <p>Rating: {product.rating} / 5</p>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No new products available</p>
                )}
            </div>
        </div>
    );
};

export default NewProduct;
