import React from "react";
import { Header } from '../../MutualComponents/Header/Header';
import { Footer } from "../../MutualComponents/Footer/Footer";
import NewsletterSection from "../../MutualComponents/Newsletter/Newsletter";
import ProductDetail from "./component/ProductDetail";
import styles from './ProductInfo.module.css';

const ProductInfo = () => {
    // Sample product data
    const product = {
        name: "Apple iPhone 16 Pro Max",
        category: "Phone",
        price: 15,
        originalPrice: 25,
        discount: "40%",
        rating: 5,
        description: "This graphic t-shirt which is perfect for any occasion. Crafted from a soft and breathable fabric, it offers superior comfort and style.",
        colors: ["#000000", "#D8D8D8", "#5A5A5A"],
        storageOptions: ["256GB", "512GB", "1TB"],
        stock: 5,
        image: "https://example.com/sample-product-image.jpg"
    };

    return (
        <main className={styles.ProductInfo}>
            <Header />
            <div className={styles.container}>
                <nav className={styles.breadcrumb}>
                    Home  Phone  Apple  iPhone
                </nav>
                <div className={styles.content}>
                    <section className={styles.productImages}>
                        <img src={product.image} alt={product.name} className={styles.mainImage} />
                        <img src={product.image} alt={`${product.name} thumbnail`} className={styles.thumbnail} />
                    </section>
                    <aside className={styles.productDetails}>
                        <h1 className={styles.productName}>{product.name}</h1>
                        <div className={styles.rating}>⭐ {product.rating} / 5</div>
                        <div className={styles.price}>
                            <span className={styles.currentPrice}>${product.price}</span>
                            <span className={styles.originalPrice}>${product.originalPrice}</span>
                            <span className={styles.discount}>{product.discount}</span>
                        </div>
                        <p className={styles.description}>{product.description}</p>
                        <div className={styles.colorSelection}>
                            <span>Select Colors:</span>
                            {product.colors.map((color, index) => (
                                <div key={index} className={styles.color} style={{ backgroundColor: color }}></div>
                            ))}
                        </div>
                        <div className={styles.storageOptions}>
                            <span>Choose Storage:</span>
                            {product.storageOptions.map((option, index) => (
                                <button key={index} className={styles.storageOption}>{option}</button>
                            ))}
                            <span className={styles.stock}>{product.stock} last products</span>
                        </div>
                        <div className={styles.quantity}>
                            <button className={styles.decrement}>-</button>
                            <span className={styles.quantityValue}>1</span>
                            <button className={styles.increment}>+</button>
                        </div>
                        <button className={styles.addToCart}>Add to Rent</button>
                    </aside>
                </div>
                <NewsletterSection />
            </div>
            <Footer />
        </main>
    );
};

export default ProductInfo;
