import React from "react";
import styles from "./Cart.module.css";

const Checkout = ({ onPay }) => {
  const handlePay = () => {
    alert("Payment successful!");
    onPay();
  };

  return (
    <div className={styles.checkout}>
      <h2 className={styles.title}>Payment Methods</h2>
      <div className={styles.paymentIcons}>

      </div>

      <form className={styles.form}>
        <h3 className={styles.formTitle}>Enter your Card!</h3>
        
        <label htmlFor="cardNumber">Card Number</label>
        <input
          id="cardNumber"
          type="text"
          placeholder="XXXX XXXX XXXX XXXX"
          className={styles.input}
          required
        />
        
        <label htmlFor="validDate">Valid Date</label>
        <input
          id="validDate"
          type="text"
          placeholder="MM/YY"
          className={styles.input}
          required
        />
        
        <label htmlFor="cvv">CVV</label>
        <input
          id="cvv"
          type="text"
          placeholder="XXX"
          className={styles.input}
          required
        />
        
        <label htmlFor="cardName">Name on Card</label>
        <input
          id="cardName"
          type="text"
          placeholder="Enter your Name"
          className={styles.input}
          required
        />
        
        <button type="button" onClick={handlePay} className={styles.payButton}>
          Pay
        </button>
      </form>
    </div>
  );
};

export default Checkout;
