import React from 'react';
import styles from './MP.module.css';


const ManageProduct = ({ image, title, size, color, price, quantity, onQuantityChange, onRemove }) => {
    const [products, setProducts] = useState([
        { id: 1, name: 'Product 1', price: 100, quantity: 20 },
        { id: 2, name: 'Product 2', price: 200, quantity: 15 },
        { id: 3, name: 'Product 3', price: 150, quantity: 30 },
        // Thêm sản phẩm khác nếu cần
      ]);
    
      return (
        <div className={styles.manageProduct}>
          <h2>Quản lý sản phẩm</h2>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Tên sản phẩm</th>
                <th>Giá</th>
                <th>Số lượng</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product.id}>
                  <td>{product.id}</td>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td>{product.quantity}</td>
                  <td>
                    <button className={styles.editButton}>Chỉnh sửa</button>
                    <button className={styles.deleteButton}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
}

export default ManageProduct;
