import React, { useState } from 'react';

const FilterSidebar = ({ setFilterCriteria, applyFilter }) => {
    const [selectedCategory, setSelectedCategory] = useState(null);

    const handleCategorySelect = (category) => {
        setSelectedCategory(category);
    };

    const handleApplyFilter = () => {
        setFilterCriteria({ category: selectedCategory });
        applyFilter();
    };

    return (
        <div style={styles.sidebar}>
            <h3 style={styles.title}>Filters</h3>
            <div style={styles.section}>
                <h4 style={styles.categoryTitle}>Category</h4>
                <ul style={styles.categoryList}>
                    <li style={styles.categoryItem} onClick={() => handleCategorySelect('Máy ảnh & Máy quay')}>
                        Máy ảnh & Máy quay
                    </li>
                    <li style={styles.categoryItem} onClick={() => handleCategorySelect('Chân máy')}>
                        Chân máy
                    </li>
                    <li style={styles.categoryItem} onClick={() => handleCategorySelect('Flycam')}>
                        Flycam
                    </li>
                    <li style={styles.categoryItem} onClick={() => handleCategorySelect('Smart phone')}>
                        Smart phone
                    </li>
                </ul>
            </div>
            <button onClick={handleApplyFilter} style={styles.applyButton}>Apply Filter</button>
        </div>
    );
};

const styles = {
    sidebar: {
        width: '250px',
        padding: '16px',
        border: '1px solid #ddd',
        borderRadius: '8px',
    },
    title: {
        fontSize: '18px',
        fontWeight: 'bold',
        marginBottom: '16px',
    },
    section: {
        marginBottom: '16px',
    },
    categoryTitle: {
        fontSize: '16px',
        fontWeight: 'bold',
        marginBottom: '8px',
    },
    categoryList: {
        listStyleType: 'none',
        padding: 0,
    },
    categoryItem: {
        padding: '8px 0',
        cursor: 'pointer',
    },
    applyButton: {
        width: '100%',
        padding: '10px 0',
        backgroundColor: 'black',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        fontSize: '16px',
        cursor: 'pointer',
    },
};

export default FilterSidebar;
