import React, { useState } from 'react';
import styles from './Filter.module.css'

const FilterBox = ({ onFilterChange }) => {
  const [price, setPrice] = useState([0, 100]);
  const [categories, setCategories] = useState({
    camera: false,
    lens: false,
    tripod: false,
    flycam: false,
  });

  // Xử lý thay đổi khi điều chỉnh giá
  const handlePriceChange = (event) => {
    const value = parseInt(event.target.value);
    setPrice((prevPrice) => [
      event.target.name === "min" ? value : prevPrice[0],
      event.target.name === "max" ? value : prevPrice[1],
    ]);
    onFilterChange({ price: [price[0], price[1]], categories });
  };

  // Xử lý khi thay đổi checkbox của loại sản phẩm
  const handleCategoryChange = (event) => {
    const { name, checked } = event.target;
    setCategories((prevCategories) => ({
      ...prevCategories,
      [name]: checked,
    }));
    onFilterChange({ price, categories: { ...categories, [name]: checked } });
  };

  return (
    <div style={styles.filterBox}>
      <h2>Bộ lọc sản phẩm</h2>

      {/* Lọc theo loại sản phẩm */}
      <div>
        <h4>Loại sản phẩm</h4>
        <label style={styles.checkboxLabel}>
          <input
            type="checkbox"
            name="camera"
            checked={categories.camera}
            onChange={handleCategoryChange}
          />
          Máy ảnh & Máy quay
        </label>
        <label style={styles.checkboxLabel}>
          <input
            type="checkbox"
            name="lens"
            checked={categories.lens}
            onChange={handleCategoryChange}
          />
          Ống kính
        </label>
        <label style={styles.checkboxLabel}>
          <input
            type="checkbox"
            name="tripod"
            checked={categories.tripod}
            onChange={handleCategoryChange}
          />
          Chân máy
        </label>
        <label style={styles.checkboxLabel}>
          <input
            type="checkbox"
            name="flycam"
            checked={categories.flycam}
            onChange={handleCategoryChange}
          />
          Flycam
        </label>
      </div>

      {/* Lọc theo mức giá */}
      <div style={styles.priceFilter}>
        <h4>Mức giá</h4>
        <div style={styles.sliderContainer}>
          <input
            type="range"
            name="min"
            min="0"
            max="1000"
            value={price[0]}
            onChange={handlePriceChange}
            style={styles.slider}
          />
          <input
            type="range"
            name="max"
            min="0"
            max="1000"
            value={price[1]}
            onChange={handlePriceChange}
            style={styles.slider}
          />
        </div>
        <div>
          Giá từ: {price[0]} - {price[1]}
        </div>
      </div>
    </div>
  );
};



export default FilterBox;
