import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { HeaderLog } from "../../LoginComponent/HeaderLog";
import styles from "./SignUp.module.css";

const Verification = () => {
    const navigate = useNavigate();
    const userEmail = localStorage.getItem("userEmail");
    const [otp, setOtp] = useState("");
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const sendOTP = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_API_URL}/auth/send-otp`, {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({ email: userEmail }),
                });

                if (!response.ok) {
                    alert("Failed to send OTP. Please try again.");
                }
            } catch (error) {
                console.error("Error sending OTP:", error);
            }
        };

        sendOTP();
    }, [userEmail]);

    const handleVerificationSubmit = async () => {
        if (!otp) {
            alert("Please enter the OTP.");
            return;
        }

        setLoading(true);

        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/auth/verify-otp`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ token: otp, email: userEmail }),
            });

            if (response.ok) {
                alert("Verification successful!");
                navigate("/congratulation");
            } else {
                const data = await response.json();
                alert(data.message || "Verification failed. Please try again.");
            }
        } catch (error) {
            console.error("Error during verification:", error);
            alert("An error occurred during verification.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className={styles.signup}>
            <HeaderLog />
            <nav className={styles.signup_container}>
                <div className={styles.signup_title}>Verification</div>
                <div className={styles.verinormal}>
                    <div>We have sent you an email with an OTP code.</div>
                    <div>
                        Enter code here <input type="text" value={otp} onChange={(e) => setOtp(e.target.value)} />
                    </div>
                </div>
            </nav>
            <div className={styles.container}>
                <button className={styles.signup_button} onClick={handleVerificationSubmit} disabled={loading}>
                    {loading ? "Verifying..." : "Submit"}
                </button>
            </div>
        </div>
    );
};

export default Verification;
