import React, { useEffect, useState } from "react";
import "./FilterSidebar.css";

const FilterSidebar = ({ setFilterCriteria, categories }) => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [expandedCategory, setExpandedCategory] = useState(null);
  const [priceRange, setPriceRange] = useState([10, 200]);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setExpandedCategory(category === expandedCategory ? null : category);
  };

  const handleSubcategorySelect = (subcategory) => {
    setSelectedCategory(subcategory);
    setExpandedCategory(null);
  };

  const handlePriceChange = (event, index) => {
    const newPriceRange = [...priceRange];
    newPriceRange[index] = Number(event.target.value);
    setPriceRange(newPriceRange);
  };

  useEffect(() => {
    // Chỉ cập nhật khi category hoặc priceRange thay đổi
    setFilterCriteria({ category: selectedCategory, priceRange });
  }, [selectedCategory, priceRange, setFilterCriteria]);

  return (
    <div className="filter-sidebar">
      <h4 className="filter-title">Filters</h4>

      {/* Category Filter */}
      <div className="filter-section">
        <h4>Category</h4>
        <ul>
          {categories.map((category) => (
            <li key={category} onClick={() => handleCategorySelect(category)}>
              {category}{" "}
              <span>{expandedCategory === category ? "▼" : "►"}</span>
            </li>
          ))}
        </ul>
      </div>

      {/* Price Filter */}
      <div className="filter-section">
        <h4>Price</h4>
        <div className="price-slider-container">
          <div className="price-slider">
            <input
              type="range"
              min="10"
              max="200"
              value={priceRange[0]}
              onChange={(e) => handlePriceChange(e, 0)}
            />
            <input
              type="range"
              min="10"
              max="200"
              value={priceRange[1]}
              onChange={(e) => handlePriceChange(e, 1)}
            />
          </div>
          <div className="price-range">
            <span>${priceRange[0]}</span>
            <span>${priceRange[1]}</span>
          </div>
        </div>
      </div>

      <button className="apply-filter-button">Apply Filter</button>
    </div>
  );
};

export default FilterSidebar;
