import React from 'react';
import styles from './MP.module.css';
import { useState } from 'react';


const ManageProduct = ({ image, title, size, color, price, quantity, onQuantityChange, onRemove }) => {
  const [products, setProducts] = useState([
    { id: 1, name: 'Product 1', price: 100, quantity: 20 },
    { id: 2, name: 'Product 2', price: 200, quantity: 15 },
    { id: 3, name: 'Product 3', price: 150, quantity: 30 }
  ]);
  const [editingProductId, setEditingProductId] = useState(null);
  const [newProduct, setNewProduct] = useState({ name: '', price: '', quantity: '' });

  const handleEditClick = (id) => {
    setEditingProductId(id);
  };

  const handleSaveClick = (id) => {
    setEditingProductId(null);
    // Thực hiện lưu dữ liệu ở đây nếu cần
  };

  const handleDeleteClick = (id) => {
    setProducts(products.filter(product => product.id !== id));
  };

  const handleInputChange = (e, id, field) => {
    const updatedProducts = products.map(product => {
      if (product.id === id) {
        return { ...product, [field]: e.target.value };
      }
      return product;
    });
    setProducts(updatedProducts);
  };

  const handleAddProduct = () => {
    const newId = products.length > 0 ? products[products.length - 1].id + 1 : 1;
    setProducts([...products, { id: newId, ...newProduct }]);
    setNewProduct({ name: '', price: '', quantity: '' });
  };

  return (
    <div className={styles.manageContainer}>
      <h2 className={styles.title}>Quản lý sản phẩm</h2>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Tên sản phẩm</th>
            <th>Giá</th>
            <th>Số lượng</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>
                {editingProductId === product.id ? (
                  <input
                    type="text"
                    value={product.name}
                    onChange={(e) => handleInputChange(e, product.id, 'name')}
                  />
                ) : (
                  product.name
                )}
              </td>
              <td>
                {editingProductId === product.id ? (
                  <input
                    type="number"
                    value={product.price}
                    onChange={(e) => handleInputChange(e, product.id, 'price')}
                  />
                ) : (
                  product.price
                )}
              </td>
              <td>
                {editingProductId === product.id ? (
                  <input
                    type="number"
                    value={product.quantity}
                    onChange={(e) => handleInputChange(e, product.id, 'quantity')}
                  />
                ) : (
                  product.quantity
                )}
              </td>
              <td>
                {editingProductId === product.id ? (
                  <button className={styles.editButton} onClick={() => handleSaveClick(product.id)}>Lưu</button>
                ) : (
                  <button className={styles.editButton} onClick={() => handleEditClick(product.id)}>Sửa</button>
                )}
                <button className={styles.deleteButton} onClick={() => handleDeleteClick(product.id)}>Xóa</button>
              </td>
            </tr>
          ))}
          <tr>
            <td>Thêm mới</td>
            <td>
              <input
                type="text"
                value={newProduct.name}
                onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
              />
            </td>
            <td>
              <input
                type="number"
                value={newProduct.price}
                onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
              />
            </td>
            <td>
              <input
                type="number"
                value={newProduct.quantity}
                onChange={(e) => setNewProduct({ ...newProduct, quantity: e.target.value })}
              />
            </td>
            <td>
              <button className={styles.addButton} onClick={handleAddProduct}>Thêm</button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default ManageProduct;
