import React from "react";
import { Header } from "../MutualComponents/Header/Header";
import { Footer } from "../MutualComponents/Footer/Footer";
import NewsletterSection from "../MutualComponents/Newsletter/Newsletter";
import FilterBox from "./Filter";
import ProductList from "./ProductList";

import styles from './Category.module.css'
import { useState, useEffect } from "react";

const Category = () => {
    const productData = [
        {
          id: 1,
          name: "Fuji XT-5",
          category: "Cameras",
          imageUrl: "https://via.placeholder.com/150?text=Fuji+XT-5",
          currentPrice: 15,
          originalPrice: 15,
          discount: 0,
        },
        {
          id: 2,
          name: "Sony ULT Tower 10",
          category: "Accessories",
          imageUrl: "https://via.placeholder.com/150?text=Sony+ULT+Tower+10",
          currentPrice: 70,
          originalPrice: 70,
          discount: 0,
        },
        {
          id: 3,
          name: "iPhone 16 Pro Max",
          category: "Smartphones",
          imageUrl: "https://via.placeholder.com/150?text=iPhone+16+Pro+Max",
          currentPrice: 10,
          originalPrice: 20,
          discount: 50,
        },
        {
          id: 4,
          name: "Skinny Fit Jeans",
          category: "Clothing",
          imageUrl: "https://via.placeholder.com/150?text=Skinny+Fit+Jeans",
          currentPrice: 240,
          originalPrice: 260,
          discount: 20,
        },
        {
          id: 5,
          name: "Checkered Shirt",
          category: "Clothing",
          imageUrl: "https://via.placeholder.com/150?text=Checkered+Shirt",
          currentPrice: 180,
          originalPrice: 180,
          discount: 0,
        },
        {
          id: 6,
          name: "Vertical Striped Shirt",
          category: "Clothing",
          imageUrl: "https://via.placeholder.com/150?text=Vertical+Striped+Shirt",
          currentPrice: 212,
          originalPrice: 232,
          discount: 25,
        },
        {
          id: 7,
          name: "Courage Graphic T-shirt",
          category: "Clothing",
          imageUrl: "https://via.placeholder.com/150?text=Courage+Graphic+T-shirt",
          currentPrice: 145,
          originalPrice: 145,
          discount: 0,
        },
        {
          id: 8,
          name: "Loose Fit Bermuda Shorts",
          category: "Clothing",
          imageUrl: "https://via.placeholder.com/150?text=Loose+Fit+Bermuda+Shorts",
          currentPrice: 80,
          originalPrice: 80,
          discount: 0,
        }
      ];
      const [products, setProducts] = useState([]);
      const [filteredProducts, setFilteredProducts] = useState([]);
      const [selectedCategory, setSelectedCategory] = useState('');
    
      useEffect(() => {
        // Load mock data
        setProducts(productData);
        setFilteredProducts(productData); // Show all products initially
      }, []);
    
      const handleFilterChange = (category) => {
        setSelectedCategory(category);
        if (category === '') {
          setFilteredProducts(products); // Show all products if no category selected
        } else {
          setFilteredProducts(products.filter(product => product.category === category));
        }
      };

    return (
        <div className={styles.category}>
            <Header/>
            <div className={styles.container}>
            <div className={styles.filter_border}>
                <div className={styles.filter_container}>
                    <div className={styles.filter_title}>Filter
                    <FilterBox onFilterChange={handleFilterChange} />
                    </div>
                    <ProductList products={filteredProducts} />
                </div>
            </div>
            
                <NewsletterSection/>
            </div>
            <Footer/>
        </div>

    );
}
export default Category;
