import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../api"; // Sử dụng instance API
import styles from "./NewProduct.module.css";

const NewProduct = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true); // Để kiểm tra trạng thái tải
    const [error, setError] = useState(false); // Kiểm tra lỗi khi gọi API
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true); // Đặt trạng thái tải
                const response = await api.get("/products?sort=new");
                // Kiểm tra nếu response.data tồn tại và là mảng hợp lệ
                if (response.data && Array.isArray(response.data)) {
                    setProducts(response.data);
                } else {
                    setProducts([]);
                }
            } catch (err) {
                console.error("Failed to fetch new products:", err);
                setError(true); // Bật trạng thái lỗi nếu có lỗi
                setProducts([]); // Đặt sản phẩm thành mảng rỗng nếu có lỗi
            } finally {
                setLoading(false); // Đặt lại trạng thái tải
            }
        };

        fetchProducts();
    }, []);

    const handleProductClick = (product) => {
        // Đảm bảo rằng bạn truyền thông tin sản phẩm đúng
        navigate(`/product-info/${product.id}`, { state: { product } });
    };

    if (loading) return <div>Loading...</div>; // Hiển thị khi đang tải
    if (error) return <div>Error loading products. Please try again later.</div>; // Hiển thị khi có lỗi

    return (
        <div className={styles.container}>
            <div className={styles.title}>
                <div className={styles.bold}>NEW</div> Products
            </div>
            <div className={styles.productContainer}>
                {products.length ? (
                    products.map((product) => (
                        <div
                            key={product.id}
                            className={styles.productFrame}
                            onClick={() => handleProductClick(product)}
                        >
                            <img
                                src={product.image || "/default-image.jpg"} // Thêm ảnh mặc định nếu không có ảnh
                                alt={product.name}
                                className={styles.productImage}
                            />
                            <div className={styles.productInfoOverlay}>
                                <h3>{product.name}</h3>
                                <p>{product.category}</p>
                                <p>
                                    From{" "}
                                    <span className={styles.originalPrice}>
                                        ${product.price}
                                    </span>
                                </p>
                                {product.discountedPrice && product.discountedPrice < product.price && (
                                    <p>
                                        <span className={styles.discountedPrice}>
                                            ${product.discountedPrice}
                                        </span>
                                    </p>
                                )}
                                <p>Rating: {product.rating || "N/A"} / 5</p> {/* Thêm điều kiện nếu rating không có */}
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No new products available</p>
                )}
            </div>
        </div>
    );
};

export default NewProduct;
