import React, { useState, useContext } from "react";
import { CartContext } from "../../context/CartContext"; // Đường dẫn tới file CartContext.js
import styles from './ProductDetail.module.css';

const ProductDetail = ({ product }) => {
  const { addToCart } = useContext(CartContext);
  const [quantity, setQuantity] = useState(1);
  const [selectedColor, setSelectedColor] = useState(product.colors[0]);
  const [selectedStorage, setSelectedStorage] = useState(product.storageOptions[0].size);

  const handleAddToCart = () => {
    const cartItem = {
      id: product.id,
      name: product.name,
      image: product.image,
      price: product.discountedPrice,
      color: selectedColor,
      size: selectedStorage,
      quantity,
    };
    addToCart(cartItem);
  };

  return (
    <div className={styles.product_detail}>
      {/* Nội dung chi tiết sản phẩm (không thay đổi) */}
      <div className={styles.Rent}>
        <div className={styles.quantity_section}>
          <button onClick={() => setQuantity(Math.max(1, quantity - 1))} className={styles.quantity_button}>-</button>
          <span className={styles.quantity}>{quantity}</span>
          <button onClick={() => setQuantity(quantity + 1)} className={styles.quantity_button}>+</button>
        </div>
        <button 
          className={styles.add_to_rent} 
          onClick={handleAddToCart}
          disabled={product.stock === 0}
        >
          {product.stock > 0 ? 'Add to Cart' : 'Out of Stock'}
        </button>
      </div>
    </div>
  );
};

export default ProductDetail;
