import React from "react";
import ProductCard from "./ProductCard";
import { useState,useEffect } from "react";
import styles from './HotItem.module.css'


const HotItem = () => {
    const [products, setProducts] = useState([]);

  // Fetch products data
  useEffect(() => {
    async function fetchProducts() {
      try {
        // Replace this with your actual API call to fetch data
        const response = await fetch('/api/products');
        const data = await response.json();

        // Sort by rental count in descending order and select top 4
        const sortedProducts = data.sort((a, b) => b.rentalCount - a.rentalCount).slice(0, 4);
        setProducts(sortedProducts);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    }
    
    fetchProducts();
  }, []);
    return (
        <div className={styles.container}>
            <div className={styles.title}>
                <div className={styles.bold}>HOT </div> Items
            </div>
            <div className={styles.productContainer}>
                
                    <div className={styles.productFrame}>
                    {products.map((product, index) => (
          <ProductCard key={index} product={product} />
        ))}
                    </div>
                
                    <div className={styles.productFrame}>
                        
                    </div>
                
                    <div className={styles.productFrame}>
                        
                    </div>
                
                    <div className={styles.productFrame}>
                        
                    </div>
                
            
            <div className={styles.viewAll}>
                <div className={styles.view_all_content}>
                    View all
                </div>
            </div>
            </div>
        </div>
    );
}


export default HotItem;
