import React, { useState } from 'react';
import styles from './Filter.module.css';

const FilterBox = ({ onFilterChange }) => {
  const [price, setPrice] = useState([50, 200]);
  const [categories, setCategories] = useState({
    camera: false,
    lens: false,
    tripod: false,
    flycam: false,
    smartphone: false,
  });
  
  const [expanded, setExpanded] = useState({
    camera: false,
    lens: false,
    tripod: false,
    flycam: false,
    smartphone: false,
  });

  const handleToggleCategory = (category) => {
    setExpanded((prevExpanded) => ({
      ...prevExpanded,
      [category]: !prevExpanded[category],
    }));
  };

  const handlePriceChange = (event) => {
    const value = parseInt(event.target.value);
    setPrice((prevPrice) => [
      event.target.name === "min" ? value : prevPrice[0],
      event.target.name === "max" ? value : prevPrice[1],
    ]);
    onFilterChange({ price: [price[0], price[1]], categories });
  };

  return (
    <div className={styles.filterBox}>


      <div className={styles.categoryFilter}>
        {Object.keys(categories).map((categoryKey) => (
          <div key={categoryKey}>
            <div className={styles.category} onClick={() => handleToggleCategory(categoryKey)}>
              <span>{categoryKey.charAt(0).toUpperCase() + categoryKey.slice(1)}</span>
              <span className={styles.expandIcon}>{expanded[categoryKey] ? '▼' : '▶'}</span>
            </div>
            {expanded[categoryKey] && (
              <div className={styles.subCategories}>
                {/* Replace these with the actual items in each category */}
                <label>
                  <input
                    type="checkbox"
                    name={`${categoryKey}_item1`}
                    onChange={(e) => onFilterChange({ price, categories: { ...categories, [`${categoryKey}_item1`]: e.target.checked } })}
                  />
                  Item 1
                </label>
                <label>
                  <input
                    type="checkbox"
                    name={`${categoryKey}_item2`}
                    onChange={(e) => onFilterChange({ price, categories: { ...categories, [`${categoryKey}_item2`]: e.target.checked } })}
                  />
                  Item 2
                </label>
              </div>
            )}
          </div>
        ))}
      </div>

      <hr />

      <div className={styles.priceFilter}>
        <h4>Price</h4>
        <div className={styles.sliderContainer}>
          <input
            type="range"
            name="min"
            min="0"
            max="1000"
            value={price[0]}
            onChange={handlePriceChange}
            className={styles.slider}
          />
          <input
            type="range"
            name="max"
            min="0"
            max="1000"
            value={price[1]}
            onChange={handlePriceChange}
            className={styles.slider}
          />
        </div>
        <div className={styles.priceDisplay}>
          <span>${price[0]}</span>
          <span>${price[1]}</span>
        </div>
      </div>

      <button className={styles.applyButton}>Apply Filter</button>
    </div>
  );
};

export default FilterBox;
