import React from 'react';
import styles from './MU.module.css';
import { useState } from 'react';

const ManageUser = () => {
    const [users, setUsers] = useState([
        { id: 1, name: 'User 1', email: 'user1@example.com', role: 'Admin' },
        { id: 2, name: 'User 2', email: 'user2@example.com', role: 'User' },
        { id: 3, name: 'User 3', email: 'user3@example.com', role: 'User' },
        // Thêm người dùng khác nếu cần
      ]);
    
      return (
        <div className={styles.manageUser}>
          <h2>Quản lý người dùng</h2>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Tên người dùng</th>
                <th>Email</th>
                <th>Vai trò</th>
                <th>Hành động</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.role}</td>
                  <td>
                    <button className={styles.editButton}>Chỉnh sửa</button>
                    <button className={styles.deleteButton}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
};

export default ManageUser;
