// src/components/Checkout/Checkout.jsx
import React, { useState } from "react";
import { Header } from "../MutualComponents/Header/Header";
import { Footer } from "../MutualComponents/Footer/Footer";
import styles from "./Cart.module.css";

export const Checkout = ({ cartItems, total, onBackToCart }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    address: "",
    cardNumber: "",
    expiryDate: "",
    cvv: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handlePay = () => {
    alert("Payment processed successfully!");
  };

  return (
    <main className={styles.checkout}>
      <Header />

      <div className={styles.container}>
        <h1 className={styles.title}>Payment Methods</h1>
        <div className={styles.form}>
          <label>
            Full Name:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Address:
            <input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Card Number:
            <input
              type="text"
              name="cardNumber"
              value={formData.cardNumber}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Expiry Date:
            <input
              type="month"
              name="expiryDate"
              value={formData.expiryDate}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            CVV:
            <input
              type="text"
              name="cvv"
              value={formData.cvv}
              onChange={handleChange}
              required
            />
          </label>
          <div className={styles.actions}>
            <button onClick={onBackToCart}>Back to Cart</button>
            <button onClick={handlePay} className={styles.payButton}>
              Pay ${total.toFixed(2)}
            </button>
          </div>
        </div>
      </div>

      <Footer />
    </main>
  );
};

export default Checkout;
