import React from "react";
import styles from './HotItem.module.css'


const HotItem = () => {
    const [phones, setPhones] = useState([]);

    useEffect(() => {
        // Giả lập dữ liệu, bạn có thể thay bằng axios để lấy từ API
        const fetchData = async () => {
            const mockData = [
                {
                    id: 1,
                    name: 'iPhone 13',
                    price: '$799',
                    description: 'Apple iPhone 13 with A15 Bionic chip',
                    imageUrl: 'https://example.com/iphone13.jpg',
                },
                {
                    id: 2,
                    name: 'Samsung Galaxy S21',
                    price: '$699',
                    description: 'Samsung Galaxy S21 with Exynos 2100',
                    imageUrl: 'https://example.com/galaxys21.jpg',
                },
                // Thêm các mẫu điện thoại khác
            ];
            setPhones(mockData);
        };
    }, []);
    return (
        <div className={styles.container}>
            <div className={styles.title}>
                <div className={styles.bold}>HOT </div> Items
            </div>
            <div className={styles.productContainer}>
                
                    <div className={styles.productFrame}>
                    {phones.map(phone => (
                <PhoneCard key={phone.id} phone={phone} />
            ))}
                    </div>
                
                    <div className={styles.productFrame}>
                        
                    </div>
                
                    <div className={styles.productFrame}>
                        
                    </div>
                
                    <div className={styles.productFrame}>
                        
                    </div>
                
            
            <div className={styles.viewAll}>
                <div className={styles.view_all_content}>
                    View all
                </div>
            </div>
            </div>
        </div>
    );
}


export default HotItem;
