import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Header } from "../MutualComponents/Header/Header";
import { Footer } from "../MutualComponents/Footer/Footer";
import NewsletterSection from "../MutualComponents/Newsletter/Newsletter";
import ProductCard from "./Product/component/ProductCard";
import FilterSidebar from "./FilterSidebar";
import styles from "./Category.module.css";

const ITEMS_PER_PAGE = 8; // Số sản phẩm mỗi trang

const Category = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1); // Trang hiện tại
    const [filterCriteria, setFilterCriteria] = useState(null);
    const navigate = useNavigate();

    // Fetch all products when component mounts
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/categories`);
                const categories = response.data;

                // Flatten products from categories
                const allProducts = categories.flatMap((category) => category.products || []);
                setProducts(allProducts);
                setFilteredProducts(allProducts); // Show all products initially
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };

        fetchProducts();
    }, []);

    // Apply filters only when filterCriteria changes
    useEffect(() => {
        if (!filterCriteria) return;

        const filtered = products.filter((product) => {
            if (
                filterCriteria.category &&
                product.category !== filterCriteria.category &&
                product.subcategory !== filterCriteria.category
            ) {
                return false;
            }
            if (
                product.price < filterCriteria.priceRange[0] ||
                product.price > filterCriteria.priceRange[1]
            ) {
                return false;
            }
            return true;
        });
        setFilteredProducts(filtered);
        setCurrentPage(1); // Reset về trang đầu khi thay đổi bộ lọc
    }, [filterCriteria, products]);

    // Pagination: Calculate products to display on the current page
    const indexOfLastProduct = currentPage * ITEMS_PER_PAGE;
    const indexOfFirstProduct = indexOfLastProduct - ITEMS_PER_PAGE;
    const currentProducts = filteredProducts.slice(indexOfFirstProduct, indexOfLastProduct);

    const handleProductClick = (productId) => {
        navigate(`/product-info/${productId}`);
    };

    const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    const totalPages = Math.ceil(filteredProducts.length / ITEMS_PER_PAGE);

    return (
        <div className={styles.category}>
            <Header />
            <div className={styles.container}>
                <div className={styles.divider}></div>
                <div className={styles.topBar}></div>
                <div className={styles.content}>
                    <div className={styles.filter_border}>
                        <div className={styles.filter_container}>
                            <FilterSidebar setFilterCriteria={setFilterCriteria} />
                        </div>
                    </div>
                    <aside className={styles.side}>
                        <div className={styles.title}>
                            <div className={styles.title}>Category</div>
                            <div className={styles.sortOptions}>
                                <label htmlFor="sort-select">Sort by:</label>
                                <select
                                    id="sort-select"
                                    onChange={(e) => {
                                        const sortType = e.target.value;
                                        let sortedProducts = [...filteredProducts];
                                        if (sortType === "price-asc") {
                                            sortedProducts.sort((a, b) => a.price - b.price);
                                        } else if (sortType === "price-desc") {
                                            sortedProducts.sort((a, b) => b.price - a.price);
                                        } else if (sortType === "rental-desc") {
                                            sortedProducts.sort((a, b) => b.rentalCount - a.rentalCount);
                                        }
                                        setFilteredProducts(sortedProducts);
                                    }}
                                >
                                    <option value="price-asc">Price: Low to High</option>
                                    <option value="price-desc">Price: High to Low</option>
                                    <option value="rental-desc">Most Popular</option>
                                </select>
                            </div>
                        </div>
                        <div className={styles.sidebar}>
                            {currentProducts.map((product) => (
                                <div key={product.id} onClick={() => handleProductClick(product.id)}>
                                    <ProductCard product={product} />
                                </div>
                            ))}
                        </div>
                    </aside>
                </div>

                {/* Pagination Section */}
                {totalPages > 1 && (
                    <div className={styles.pagination}>
                        <button
                            onClick={() => handlePageChange(currentPage - 1)}
                            disabled={currentPage === 1}
                            className={styles.paginationButton}
                        >
                            &larr; Previous
                        </button>
                        {[...Array(totalPages).keys()].map((number) => (
                            <button
                                key={number}
                                onClick={() => handlePageChange(number + 1)}
                                className={`${styles.pageNumber} ${
                                    currentPage === number + 1 ? styles.active : ""
                                }`}
                            >
                                {number + 1}
                            </button>
                        ))}
                        <button
                            onClick={() => handlePageChange(currentPage + 1)}
                            disabled={currentPage === totalPages}
                            className={styles.paginationButton}
                        >
                            Next &rarr;
                        </button>
                    </div>
                )}

                <NewsletterSection />
            </div>
            <Footer />
        </div>
    );
};

export default Category;
