import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../../api"; // Sử dụng instance API đã cấu hình
import { Header } from "../../MutualComponents/Header/Header";
import { Footer } from "../../MutualComponents/Footer/Footer";
import NewsletterSection from "../../MutualComponents/Newsletter/Newsletter";
import ProductDetail from "./component/ProductDetail";
import styles from "./ProductInfo.module.css";

const ProductInfo = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await api.get(`/products/${productId}`);
                setProduct(response.data); // Lưu sản phẩm vào state
            } catch (err) {
                console.error("Failed to fetch product details:", err);
                setError(true); // Đặt cờ lỗi nếu không thể lấy sản phẩm
            } finally {
                setLoading(false); // Kết thúc trạng thái tải
            }
        };

        fetchProduct();
    }, [productId]);

    // Hiển thị trạng thái đang tải
    if (loading) {
        return (
            <main className={styles.ProductInfo}>
                <Header />
                <div className={styles.loadingContainer}>Loading...</div>
                <Footer />
            </main>
        );
    }

    // Hiển thị khi không tìm thấy sản phẩm hoặc có lỗi
    if (error || !product) {
        return (
            <main className={styles.ProductInfo}>
                <Header />
                <div className={styles.container}>
                    <div className={styles.errorMessage}>
                        Product not found. Please check the product ID.
                    </div>
                </div>
                <NewsletterSection />
                <Footer />
            </main>
        );
    }

    // Hiển thị thông tin sản phẩm nếu dữ liệu hợp lệ
    return (
        <main className={styles.ProductInfo}>
            <Header />
            <div className={styles.container}>
                <ProductDetail product={product} />
                <NewsletterSection />
            </div>
            <Footer />
        </main>
    );
};

export default ProductInfo;
