import React from 'react';
import styles from './MP.module.css';
import { useState } from 'react';


const ManageProduct = ({ image, title, size, color, price, quantity, onQuantityChange, onRemove }) => {
  const [products, setProducts] = useState([
    { id: 1, name: 'Product 1', price: 100, quantity: 20 },
    { id: 2, name: 'Product 2', price: 200, quantity: 15 },
    { id: 3, name: 'Product 3', price: 150, quantity: 30 },
  ]);

  const [isEditing, setIsEditing] = useState(null);
  const [newProduct, setNewProduct] = useState({ name: '', price: '', quantity: '' });

  const handleEditChange = (e, field, index) => {
    const updatedProducts = [...products];
    updatedProducts[index][field] = e.target.value;
    setProducts(updatedProducts);
  };

  const handleDelete = (index) => {
    const updatedProducts = products.filter((_, i) => i !== index);
    setProducts(updatedProducts);
  };

  const handleAddProduct = () => {
    const newId = products.length ? products[products.length - 1].id + 1 : 1;
    setProducts([...products, { id: newId, ...newProduct }]);
    setNewProduct({ name: '', price: '', quantity: '' });
  };

  return (
    <div className={styles.manageContainer}>
      <h2 className={styles.title}>Quản lý sản phẩm</h2>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Tên sản phẩm</th>
            <th>Giá</th>
            <th>Số lượng</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>
                <input
                  type="text"
                  value={product.name}
                  onChange={(e) => handleEditChange(e, 'name', index)}
                  disabled={isEditing !== product.id}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={product.price}
                  onChange={(e) => handleEditChange(e, 'price', index)}
                  disabled={isEditing !== product.id}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={product.quantity}
                  onChange={(e) => handleEditChange(e, 'quantity', index)}
                  disabled={isEditing !== product.id}
                />
              </td>
              <td>
                {isEditing === product.id ? (
                  <button onClick={() => setIsEditing(null)} className={styles.editButton}>
                    Lưu
                  </button>
                ) : (
                  <button onClick={() => setIsEditing(product.id)} className={styles.editButton}>
                    Sửa
                  </button>
                )}
                <button onClick={() => handleDelete(index)} className={styles.deleteButton}>
                  Xóa
                </button>
              </td>
            </tr>
          ))}
          <tr>
            <td>Thêm mới</td>
            <td>
              <input
                type="text"
                value={newProduct.name}
                onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
              />
            </td>
            <td>
              <input
                type="number"
                value={newProduct.price}
                onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
              />
            </td>
            <td>
              <input
                type="number"
                value={newProduct.quantity}
                onChange={(e) => setNewProduct({ ...newProduct, quantity: e.target.value })}
              />
            </td>
            <td>
              <button onClick={handleAddProduct} className={styles.addButton}>
                Thêm
              </button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default ManageProduct;
