import React, { useEffect, useState, useCallback } from "react";
import { Header } from "../MutualComponents/Header/Header";
import { CartItem } from "./components/CartItem/CartItem";
import OrderSummary from "./components/OrderSummary/OrderSummary";
import NewsletterSection from "../MutualComponents/Newsletter/Newsletter";
import { Footer } from "../MutualComponents/Footer/Footer";
import Checkout from "./Checkout";
import { useLocation, useNavigate } from "react-router-dom";
import api from "../../api"; // Sử dụng Axios instance
import styles from "./Cart.module.css";

export const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const location = useLocation();
  const navigate = useNavigate();

  // Fetch cart items
  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await api.get("/cart");
        setCartItems(response.data.items);
      } catch (error) {
        setError("Failed to load cart items. Please try again.");
        console.error("Fetch cart items error:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  // Update item quantity
  const updateQuantity = useCallback(
    async (id, newQuantity) => {
      try {
        const response = await api.put(`/cart/${id}`, { quantity: newQuantity });
        setCartItems(response.data.items);
      } catch (error) {
        alert("Failed to update item quantity. Please try again.");
        console.error("Update quantity error:", error);
      }
    },
    [setCartItems]
  );

  // Remove item from cart
  const removeItem = useCallback(
    async (id) => {
      try {
        const response = await api.delete(`/cart/${id}`);
        setCartItems(response.data.items);
      } catch (error) {
        alert("Failed to remove item. Please try again.");
        console.error("Remove item error:", error);
      }
    },
    [setCartItems]
  );

  // Calculate costs
  const subtotal = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const discount = subtotal * 0.2;
  const deliveryFee = subtotal > 0 ? 15 : 0; // Delivery fee only applies if cart is not empty
  const total = subtotal - discount + deliveryFee;

  const handleCheckout = () => {
    navigate("/cart/checkout");
  };

  if (loading) {
    return <div>Loading cart...</div>;
  }

  if (error) {
    return <div className={styles.error}>{error}</div>;
  }

  return (
    <main className={styles.cart}>
      <Header />

      <div className={styles.container}>
        <div className={styles.divider}></div>

        <h1 className={styles.title}>
          {location.pathname === "/cart/checkout" ? "Payment Methods" : "Your Cart"}
        </h1>

        <div className={styles.content}>
          <section className={styles.cartItems}>
            {location.pathname === "/cart/checkout" ? (
              <Checkout onPay={() => alert("Payment successful!")} />
            ) : cartItems.length ? (
              cartItems.map((item) => (
                <CartItem
                  key={item.id}
                  {...item}
                  onQuantityChange={(newQuantity) => updateQuantity(item.id, newQuantity)}
                  onRemove={() => removeItem(item.id)}
                />
              ))
            ) : (
              <p>Your cart is empty!</p>
            )}
          </section>

          <aside className={styles.sidebar}>
            <OrderSummary
              subtotal={subtotal}
              discount={discount}
              deliveryFee={deliveryFee}
              total={total}
              isCheckout={location.pathname === "/cart/checkout"}
              onCheckout={handleCheckout}
            />
          </aside>
        </div>

        <NewsletterSection />
      </div>
      <Footer />
    </main>
  );
};
