import React from 'react';
import styles from './MP.module.css';
import { useState } from 'react';


const ManageProduct = ({ image, title, size, color, price, quantity, onQuantityChange, onRemove }) => {
  const [products, setProducts] = useState([
    { id: 1, name: 'Product 1', price: 100, quantity: 20 },
    { id: 2, name: 'Product 2', price: 200, quantity: 15 },
    { id: 3, name: 'Product 3', price: 150, quantity: 30 },
  ]);

  const [isEditMode, setIsEditMode] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);

  const handleDeleteRows = () => {
    setProducts(products.filter((product) => !product.selected));
  };

  const toggleEditMode = () => {
    setIsEditMode(!isEditMode);
  };

  const toggleDeleteMode = () => {
    setIsDeleteMode(!isDeleteMode);
  };

  const handleInputChange = (e, index, field) => {
    const updatedProducts = [...products];
    updatedProducts[index][field] = e.target.value;
    setProducts(updatedProducts);
  };

  const handleCheckboxChange = (index) => {
    const updatedProducts = [...products];
    updatedProducts[index].selected = !updatedProducts[index].selected;
    setProducts(updatedProducts);
  };

  return (
    <div className={styles.manageContainer}>
      <h2 className={styles.title}>Quản lý Sản phẩm</h2>
      <div className={styles.buttonContainer}>
        <button onClick={toggleDeleteMode} className={styles.deleteButton}>Xóa</button>
        <button onClick={toggleEditMode} className={styles.editButton}>Sửa</button>
      </div>
      <table className={styles.table}>
        <thead>
          <tr>
            {isDeleteMode && <th>Chọn</th>}
            <th>ID</th>
            <th>Tên sản phẩm</th>
            <th>Giá</th>
            <th>Số lượng</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={product.id}>
              {isDeleteMode && (
                <td>
                  <input
                    type="checkbox"
                    checked={product.selected || false}
                    onChange={() => handleCheckboxChange(index)}
                  />
                </td>
              )}
              <td>{product.id}</td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.name}
                    onChange={(e) => handleInputChange(e, index, 'name')}
                  />
                ) : (
                  product.name
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="number"
                    value={product.price}
                    onChange={(e) => handleInputChange(e, index, 'price')}
                  />
                ) : (
                  product.price
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="number"
                    value={product.quantity}
                    onChange={(e) => handleInputChange(e, index, 'quantity')}
                  />
                ) : (
                  product.quantity
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {isDeleteMode && (
        <button onClick={handleDeleteRows} className={styles.deleteButton}>
          Xóa các hàng đã chọn
        </button>
      )}
    </div>
  );
}

export default ManageProduct;
