import React from "react";
import { useLocation, useNavigate } from "react-router-dom"; // Import useLocation to get passed product data
import { Header } from '../../MutualComponents/Header/Header';
import { Footer } from "../../MutualComponents/Footer/Footer";
import NewsletterSection from "../../MutualComponents/Newsletter/Newsletter";
import ProductDetail from "./component/ProductDetail";
import styles from './ProductInfo.module.css';

const ProductInfo = () => {
    const location = useLocation(); // Get the product data from the location state
    const navigate = useNavigate();
    
    const product = location.state?.product;

    if (!product) {
        // If no product is passed, redirect back to Category page
        navigate('/');
        return null;
    }

    return (
        <main className={styles.ProductInfo}>
            <Header />
            <div className={styles.container}>
                <div className={styles.content}>
                    <div className={styles.detail}>
                        <ProductDetail product={product} />
                    </div>
                    <NewsletterSection />
                </div>
            </div>
            <Footer />
        </main>
    );
};

export default ProductInfo;
