import React, { useState } from 'react';

const FilterSidebar = ({ setFilterCriteria, applyFilter }) => {
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [priceRange, setPriceRange] = useState([0, 300]);
    const [expandedCategories, setExpandedCategories] = useState({});

    const handleCategorySelect = (category) => {
        setSelectedCategory(category);
    };

    const toggleCategory = (category) => {
        setExpandedCategories((prev) => ({
            ...prev,
            [category]: !prev[category],
        }));
    };

    const handlePriceChange = (event, index) => {
        const newPriceRange = [...priceRange];
        newPriceRange[index] = Number(event.target.value);
        setPriceRange(newPriceRange);
    };

    const handleApplyFilter = () => {
        setFilterCriteria({ category: selectedCategory, priceRange });
        applyFilter();
    };

    return (
        <div style={styles.sidebar}>
            <h4 style={styles.title}>Filters</h4>

            {/* Category Section */}
            <div style={styles.section}>
                <ul style={styles.categoryList}>
                    {['Máy ảnh & Máy quay', 'Ống kính', 'Chân máy', 'Flycam', 'Smart phone'].map((category) => (
                        <li key={category} style={styles.categoryItem} onClick={() => toggleCategory(category)}>
                            <span style={styles.categoryHeader}>
                                {category}
                                <span style={styles.arrow}>
                                    {expandedCategories[category] ? '▲' : '▼'}
                                </span>
                            </span>
                            {expandedCategories[category] && (
                                <ul style={styles.subcategoryList}>
                                    {/* Example subcategories */}
                                    <li style={styles.subcategoryItem} onClick={() => handleCategorySelect('Sony')}>Sony</li>
                                    <li style={styles.subcategoryItem} onClick={() => handleCategorySelect('Fujifilm')}>Fujifilm</li>
                                    <li style={styles.subcategoryItem} onClick={() => handleCategorySelect('Nikon')}>Nikon</li>
                                </ul>
                            )}
                        </li>
                    ))}
                </ul>
            </div>

            {/* Price Filter */}
            <div style={styles.section}>
                <h4 style={styles.categoryTitle}>Price</h4>
                <div style={styles.sliderContainer}>
                    <input
                        type="range"
                        min="0"
                        max="300"
                        value={priceRange[0]}
                        onChange={(e) => handlePriceChange(e, 0)}
                        style={styles.slider}
                    />
                    <input
                        type="range"
                        min="0"
                        max="300"
                        value={priceRange[1]}
                        onChange={(e) => handlePriceChange(e, 1)}
                        style={styles.slider}
                    />
                </div>
                <div style={styles.priceRange}>
                    <span>${priceRange[0]}</span>
                    <span>${priceRange[1]}</span>
                </div>
            </div>

            {/* Apply Button */}
            <button onClick={handleApplyFilter} style={styles.applyButton}>Apply Filter</button>
        </div>
    );
};

const styles = {
    sidebar: {
        fontFamily: 'Lato, sans-serif',
    },
    title: {
        fontSize: '18px',
        fontWeight: 'bold',
        marginBottom: '16px',
    },
    section: {
        marginBottom: '16px',
    },
    categoryList: {
        listStyleType: 'none',
        padding: 0,
    },
    categoryItem: {
        padding: '8px 0',
        cursor: 'pointer',
    },
    categoryHeader: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        fontSize: '16px',
        fontWeight: 'bold',
    },
    arrow: {
        fontSize: '12px',
        paddingLeft: '8px', // Space between category name and arrow
    },
    subcategoryList: {
        listStyleType: 'none',
        padding: '8px 0 0 16px',
        margin: 0,
    },
    subcategoryItem: {
        padding: '4px 0',
        cursor: 'pointer',
        fontSize: '14px',
    },
    sliderContainer: {
        display: 'flex',
        flexDirection: 'column',
        gap: '8px',
        marginBottom: '8px',
    },
    slider: {
        appearance: 'none',
        width: '100%',
        height: '5px',
        background: '#ddd',
        outline: 'none',
        opacity: '0.7',
        transition: 'opacity .15s ease-in-out',
    },
    priceRange: {
        display: 'flex',
        justifyContent: 'space-between',
        fontSize: '14px',
    },
    applyButton: {
        width: '100%',
        padding: '10px 0',
        backgroundColor: 'black',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        fontSize: '16px',
        cursor: 'pointer',
    },
};

export default FilterSidebar;
