import React, { useState } from 'react';
import styles from './MP.module.css';

const ManageProduct = () => {
  const [products, setProducts] = useState([
    { id: 1, name: 'Product 1', type: 'Type A', price: 100, quantity: 10, status: 'Available', notes: 'N/A' },
    { id: 2, name: 'Product 2', type: 'Type B', price: 200, quantity: 5, status: 'Out of Stock', notes: 'Reorder soon' },
  ]);

  const [filterField, setFilterField] = useState(''); // Selected filter attribute
  const [filterValue, setFilterValue] = useState(''); // Selected filter value

  const [isEditMode, setIsEditMode] = useState(false);
  const [isAddMode, setIsAddMode] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    id: '',
    name: '',
    type: '',
    price: '',
    quantity: '',
    status: '',
    notes: ''
  });

  const handleFilterFieldChange = (e) => {
    setFilterField(e.target.value);
    setFilterValue(''); // Reset filter value when filter field changes
  };

  const handleFilterValueChange = (e) => {
    setFilterValue(e.target.value);
  };

  const filteredProducts = products.filter((product) => {
    if (!filterField || !filterValue) return true;
    return String(product[filterField]).toLowerCase() === filterValue.toLowerCase();
  });

  const getUniqueFilterValues = (field) => {
    const values = products.map((product) => product[field]);
    return [...new Set(values)];
  };

  const toggleEditMode = () => setIsEditMode(!isEditMode);
  const toggleAddMode = () => setIsAddMode(!isAddMode);
  const toggleDeleteMode = () => setIsDeleteMode(!isDeleteMode);

  const handleInputChange = (e, index, field) => {
    const updatedProducts = [...products];
    updatedProducts[index][field] = e.target.value;
    setProducts(updatedProducts);
  };

  const handleNewProductChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const addNewProduct = () => {
    setProducts([...products, { ...newProduct, id: products.length + 1 }]);
    setIsAddMode(false);
    setNewProduct({ id: '', name: '', type: '', price: '', quantity: '', status: '', notes: '' });
  };

  const handleSelectProduct = (id) => {
    setSelectedProducts((prev) =>
      prev.includes(id) ? prev.filter((pid) => pid !== id) : [...prev, id]
    );
  };

  const deleteSelectedProducts = () => {
    setProducts(products.filter((product) => !selectedProducts.includes(product.id)));
    setSelectedProducts([]);
    setIsDeleteMode(false);
  };

  return (
    <div className={styles.manageContainer}>
      <div className={styles.buttonContainer}>
      <select value={filterField} onChange={handleFilterFieldChange} className={styles.filterSelect}>
        <option value="">Filter</option>
        <option value="id">ID</option>
        <option value="name">Product Name</option>
        <option value="type">Type</option>
        <option value="price">Price</option>
        <option value="status">Status</option>
        <option value="notes">Notes</option>
      </select>
      <select
        value={filterValue}
        onChange={handleFilterValueChange}
        className={styles.filterSelect}
        disabled={!filterField}
      >
        <option value="">Select Value</option>
        {filterField &&
          getUniqueFilterValues(filterField).map((value, index) => (
            <option key={index} value={value}>
              {value}
            </option>
          ))}
      </select>
      <div className={styles.header}>
        <div className={styles.buttonContainer}>
          <button onClick={toggleAddMode} className={styles.addButton}>Add</button>
          <button onClick={toggleEditMode} className={styles.editButton}>Edit</button>
          <button onClick={toggleDeleteMode} className={styles.deleteButton}>Delete</button>
        </div>
        </div>
      </div>

      <table className={styles.table}>
        <thead>
          <tr>
            {isDeleteMode && <th>Select</th>}
            <th>ID</th>
            <th>Name</th>
            <th>Type</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Status</th>
            <th>Notes</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map((product, index) => (
            <tr key={product.id}>
              {isDeleteMode && (
                <td>
                  <input
                    type="checkbox"
                    checked={selectedProducts.includes(product.id)}
                    onChange={() => handleSelectProduct(product.id)}
                  />
                </td>
              )}
              <td>{product.id}</td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.name}
                    onChange={(e) => handleInputChange(e, index, 'name')}
                    className={styles.inputField}
                  />
                ) : (
                  product.name
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.type}
                    onChange={(e) => handleInputChange(e, index, 'type')}
                    className={styles.inputField}
                  />
                ) : (
                  product.type
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="number"
                    value={product.price}
                    onChange={(e) => handleInputChange(e, index, 'price')}
                    className={styles.inputField}
                  />
                ) : (
                  product.price
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="number"
                    value={product.quantity}
                    onChange={(e) => handleInputChange(e, index, 'quantity')}
                    className={styles.inputField}
                  />
                ) : (
                  product.quantity
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.status}
                    onChange={(e) => handleInputChange(e, index, 'status')}
                    className={styles.inputField}
                  />
                ) : (
                  product.status
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.notes}
                    onChange={(e) => handleInputChange(e, index, 'notes')}
                    className={styles.inputField}
                  />
                ) : (
                  product.notes
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isDeleteMode && (
        <button onClick={deleteSelectedProducts} className={styles.confirmDeleteButton}>Confirm Delete</button>
      )}

      {isAddMode && (
        <div className={styles.popup}>
          <div className={styles.popupContent}>
            <h3>Add Product</h3>
            <div className={styles.popupFormRow}>
              <label className={styles.popupLabel}>ID:</label>
              <input
                type="text"
                name="id"
                value={newProduct.id}
                onChange={handleNewProductChange}
                className={styles.popupInput}
              />
            </div>
            <div className={styles.popupFormRow}>
              <label className={styles.popupLabel}>Name:</label>
              <input
                type="text"
                name="name"
                value={newProduct.name}
                onChange={handleNewProductChange}
                className={styles.popupInput}
              />
            </div>
            <div className={styles.popupFormRow}>
              <label className={styles.popupLabel}>Type:</label>
              <input
                type="text"
                name="type"
                value={newProduct.type}
                onChange={handleNewProductChange}
                className={styles.popupInput}
              />
            </div>
            <div className={styles.popupFormRow}>
              <label className={styles.popupLabel}>Price:</label>
              <input
                type="number"
                name="price"
                value={newProduct.price}
                onChange={handleNewProductChange}
                className={styles.popupInput}
              />
            </div>
            <div className={styles.popupFormRow}>
              <label className={styles.popupLabel}>Quantity:</label>
              <input
                type="number"
                name="quantity"
                value={newProduct.quantity}
                onChange={handleNewProductChange}
                className={styles.popupInput}
              />
            </div>
            <div className={styles.popupFormRow}>
              <label className={styles.popupLabel}>Status:</label>
              <input
                type="text"
                name="status"
                value={newProduct.status}
                onChange={handleNewProductChange}
                className={styles.popupInput}
              />
            </div>
            <div className={styles.popupFormRow}>
              <label className={styles.popupLabel}>Notes:</label>
              <input
                type="text"
                name="notes"
                value={newProduct.notes}
                onChange={handleNewProductChange}
                className={styles.popupInput}
              />
            </div>
            <button onClick={addNewProduct} className={styles.saveButton}>Save</button>
            <button onClick={toggleAddMode} className={styles.cancelButton}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageProduct;
