import React from 'react';
import styles from './MP.module.css';
import { useState } from 'react';


const ManageProduct = ({ image, title, size, color, price, quantity, onQuantityChange, onRemove }) => {
  const [products, setProducts] = useState([
    { id: 1, name: 'Product 1', type: 'Type A', price: 100, quantity: 10, status: 'Available', notes: 'N/A' },
    { id: 2, name: 'Product 2', type: 'Type B', price: 200, quantity: 5, status: 'Out of Stock', notes: 'Reorder soon' },
  ]);

  const [isEditMode, setIsEditMode] = useState(false);
  const [isAddMode, setIsAddMode] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    id: '',
    name: '',
    type: '',
    price: '',
    quantity: '',
    status: '',
    notes: ''
  });

  const toggleEditMode = () => setIsEditMode(!isEditMode);
  const toggleAddMode = () => setIsAddMode(!isAddMode);
  const toggleDeleteMode = () => setIsDeleteMode(!isDeleteMode);

  const handleInputChange = (e, index, field) => {
    const updatedProducts = [...products];
    updatedProducts[index][field] = e.target.value;
    setProducts(updatedProducts);
  };

  const handleNewProductChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const addNewProduct = () => {
    setProducts([...products, { ...newProduct, id: products.length + 1 }]);
    setIsAddMode(false);
    setNewProduct({ id: '', name: '', type: '', price: '', quantity: '', status: '', notes: '' });
  };

  const handleSelectProduct = (id) => {
    setSelectedProducts(prev =>
      prev.includes(id) ? prev.filter(pid => pid !== id) : [...prev, id]
    );
  };

  const deleteSelectedProducts = () => {
    setProducts(products.filter(product => !selectedProducts.includes(product.id)));
    setSelectedProducts([]);
    setIsDeleteMode(false);
  };

  return (
    <div className={styles.tableContainer}>
      <div className={styles.header}>
        <h2 className={styles.title}>Quản lý Sản phẩm</h2>
        <div>
          <button onClick={toggleAddMode} className={styles.addButton}>Thêm</button>
          <button onClick={toggleEditMode} className={styles.editButton}>Sửa</button>
          <button onClick={toggleDeleteMode} className={styles.deleteButton}>Xóa</button>
        </div>
      </div>

      <table className={styles.table}>
        <thead>
          <tr>
            {isDeleteMode && <th>Chọn</th>}
            <th>ID</th>
            <th>Tên sản phẩm</th>
            <th>Loại</th>
            <th>Giá</th>
            <th>Số lượng</th>
            <th>Tình trạng</th>
            <th>Ghi chú</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={product.id}>
              {isDeleteMode && (
                <td>
                  <input
                    type="checkbox"
                    checked={selectedProducts.includes(product.id)}
                    onChange={() => handleSelectProduct(product.id)}
                  />
                </td>
              )}
              <td>{product.id}</td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.name}
                    onChange={(e) => handleInputChange(e, index, 'name')}
                    className={styles.inputField}
                  />
                ) : (
                  product.name
                )}
              </td>
              <td>{isEditMode ? (
                  <input
                    type="text"
                    value={product.type}
                    onChange={(e) => handleInputChange(e, index, 'type')}
                    className={styles.inputField}
                  />
                ) : (
                  product.type
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="number"
                    value={product.price}
                    onChange={(e) => handleInputChange(e, index, 'price')}
                    className={styles.inputField}
                  />
                ) : (
                  product.price
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="number"
                    value={product.quantity}
                    onChange={(e) => handleInputChange(e, index, 'quantity')}
                    className={styles.inputField}
                  />
                ) : (
                  product.quantity
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.status}
                    onChange={(e) => handleInputChange(e, index, 'status')}
                    className={styles.inputField}
                  />
                ) : (
                  product.status
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.notes}
                    onChange={(e) => handleInputChange(e, index, 'notes')}
                    className={styles.inputField}
                  />
                ) : (
                  product.notes
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isDeleteMode && (
        <button onClick={deleteSelectedProducts} className={styles.confirmDeleteButton}>Xác nhận xóa</button>
      )}

{isAddMode && (
  <div className={styles.popup}>
    <div className={styles.popupContent}>
      <h3>Add</h3>
      <div className={styles.popupFormRow}>
        <label className={styles.popupLabel}>ID:</label>
        <input type="text" name="id" value={newProduct.id} onChange={handleNewProductChange} className={styles.popupInput} />
      </div>
      <div className={styles.popupFormRow}>
        <label className={styles.popupLabel}>Tên sản phẩm:</label>
        <input type="text" name="name" value={newProduct.name} onChange={handleNewProductChange} className={styles.popupInput} />
      </div>
      <div className={styles.popupFormRow}>
        <label className={styles.popupLabel}>Loại:</label>
        <input type="text" name="type" value={newProduct.type} onChange={handleNewProductChange} className={styles.popupInput} />
      </div>
      <div className={styles.popupFormRow}>
        <label className={styles.popupLabel}>Giá:</label>
        <input type="number" name="price" value={newProduct.price} onChange={handleNewProductChange} className={styles.popupInput} />
      </div>
      <div className={styles.popupFormRow}>
        <label className={styles.popupLabel}>Số lượng:</label>
        <input type="number" name="quantity" value={newProduct.quantity} onChange={handleNewProductChange} className={styles.popupInput} />
      </div>
      <div className={styles.popupFormRow}>
        <label className={styles.popupLabel}>Tình trạng:</label>
        <input type="text" name="status" value={newProduct.status} onChange={handleNewProductChange} className={styles.popupInput} />
      </div>
      <div className={styles.popupFormRow}>
        <label className={styles.popupLabel}>Ghi chú:</label>
        <input type="text" name="notes" value={newProduct.notes} onChange={handleNewProductChange} className={styles.popupInput} />
      </div>
      <button onClick={addNewProduct} className={styles.saveButton}>Lưu</button>
      <button onClick={toggleAddMode} className={styles.cancelButton}>Hủy</button>
    </div>
  </div>
)}
    </div>
  );
}

export default ManageProduct;
