import React, { useState } from 'react';
import styles from './Filter.module.css';

const FilterBox = ({ onFilterChange }) => {
  const [price, setPrice] = useState([50, 200]);
  const [categories, setCategories] = useState({
    camera: false,
    lens: false,
    tripod: false,
    flycam: false,
    smartphone: false,
  });

  const handlePriceChange = (event) => {
    const value = parseInt(event.target.value);
    setPrice((prevPrice) => [
      event.target.name === "min" ? value : prevPrice[0],
      event.target.name === "max" ? value : prevPrice[1],
    ]);
    onFilterChange({ price: [price[0], price[1]], categories });
  };

  const handleCategoryChange = (event) => {
    const { name, checked } = event.target;
    setCategories((prevCategories) => ({
      ...prevCategories,
      [name]: checked,
    }));
    onFilterChange({ price, categories: { ...categories, [name]: checked } });
  };

  return (
    <div className={styles.filterBox}>



    <div className={styles.categoryFilter}>
        <label className={styles.category}>
        Máy ảnh & Máy quay
        <input
            type="checkbox"
            name="camera"
            checked={categories.camera}
            onChange={handleCategoryChange}
        />
        </label>
        <label className={styles.category}>
        Ống kính
        <input
            type="checkbox"
            name="lens"
            checked={categories.lens}
            onChange={handleCategoryChange}
        />
        </label>
        <label className={styles.category}>
        Chân máy
        <input
            type="checkbox"
            name="tripod"
            checked={categories.tripod}
            onChange={handleCategoryChange}
        />
        </label>
        <label className={styles.category}>
        Flycam
        <input
            type="checkbox"
            name="flycam"
            checked={categories.flycam}
            onChange={handleCategoryChange}
        />
        </label>
        <label className={styles.category}>
        Smart phone
        <input
            type="checkbox"
            name="smartphone"
            checked={categories.smartphone}
            onChange={handleCategoryChange}
        />
        </label>
    </div>

    <hr />

    <div className={styles.priceFilter}>
        <h4>Price</h4>
        <div className={styles.sliderContainer}>
        <input
            type="range"
            name="min"
            min="0"
            max="1000"
            value={price[0]}
            onChange={handlePriceChange}
            className={styles.slider}
        />
        <input
            type="range"
            name="max"
            min="0"
            max="1000"
            value={price[1]}
            onChange={handlePriceChange}
            className={styles.slider}
        />
        </div>
        <div className={styles.priceDisplay}>
        <span>${price[0]}</span>
        <span>${price[1]}</span>
        </div>
    </div>

    <button className={styles.applyButton}>Apply Filter</button>
    </div>
);
};

export default FilterBox;
