import React, { useState, useEffect } from 'react';
import styles from './MP.module.css';

const ManageProduct = () => {
  const [products, setProducts] = useState([]);
  const [filterField, setFilterField] = useState('');
  const [filterValue, setFilterValue] = useState('');
  const [isEditMode, setIsEditMode] = useState(false);
  const [isAddMode, setIsAddMode] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    id: '',
    name: '',
    price: '',
    description: '',
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch('/products');
      const data = await response.json();
      if (data.statusCode === 200) {
        setProducts(data.data);
      }
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleFilterFieldChange = (e) => {
    setFilterField(e.target.value);
    setFilterValue('');
  };

  const handleFilterValueChange = (e) => {
    setFilterValue(e.target.value);
  };

  const filteredProducts = products.filter((product) => {
    if (!filterField || !filterValue) return true;
    return String(product[filterField]).toLowerCase().includes(filterValue.toLowerCase());
  });

  const handleInputChange = (e, index, field) => {
    const updatedProducts = [...products];
    updatedProducts[index][field] = e.target.value;
    setProducts(updatedProducts);
  };

  const handleNewProductChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const addNewProduct = async () => {
    try {
      const response = await fetch('/products', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newProduct),
      });
      const data = await response.json();
      if (data.statusCode === 201) {
        fetchProducts();
        setIsAddMode(false);
        setNewProduct({ id: '', name: '', price: '', description: '' });
      }
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const updateProduct = async (id) => {
    const productToUpdate = products.find((product) => product.id === id);
    try {
      const response = await fetch(`/products/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(productToUpdate),
      });
      const data = await response.json();
      if (data.statusCode === 200) {
        fetchProducts();
        setIsEditMode(false);
      }
    } catch (error) {
      console.error('Error updating product:', error);
    }
  };

  const deleteSelectedProducts = async () => {
    try {
      await Promise.all(
        selectedProducts.map((id) =>
          fetch(`/products/${id}`, {
            method: 'DELETE',
          })
        )
      );
      fetchProducts();
      setSelectedProducts([]);
      setIsDeleteMode(false);
    } catch (error) {
      console.error('Error deleting products:', error);
    }
  };

  return (
    <div className={styles.manageContainer}>
      <div className={styles.controls}>
        <select value={filterField} onChange={handleFilterFieldChange} className={styles.filter}>
          <option value="">Filter By</option>
          <option value="id">ID</option>
          <option value="name">Name</option>
          <option value="price">Price</option>
        </select>
        <input
          type="text"
          value={filterValue}
          onChange={handleFilterValueChange}
          placeholder="Enter filter value"
          className={styles.filter}
          disabled={!filterField}
        />
        <button onClick={() => setIsAddMode(!isAddMode)} className={styles.addButton}>Add</button>
        <button onClick={() => setIsEditMode(!isEditMode)} className={styles.editButton}>Edit</button>
        <button onClick={() => setIsDeleteMode(!isDeleteMode)} className={styles.deleteButton}>Delete</button>
      </div>

      <table className={styles.table}>
        <thead>
          <tr>
            {isDeleteMode && <th>Select</th>}
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Description</th>
            {isEditMode && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map((product, index) => (
            <tr key={product.id}>
              {isDeleteMode && (
                <td>
                  <input
                    type="checkbox"
                    checked={selectedProducts.includes(product.id)}
                    onChange={() =>
                      setSelectedProducts((prev) =>
                        prev.includes(product.id) ? prev.filter((id) => id !== product.id) : [...prev, product.id]
                      )
                    }
                  />
                </td>
              )}
              <td>{product.id}</td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.name}
                    onChange={(e) => handleInputChange(e, index, 'name')}
                    className={styles.inputField}
                  />
                ) : (
                  <span className={styles.textField}>{product.name}</span>
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="number"
                    value={product.price}
                    onChange={(e) => handleInputChange(e, index, 'price')}
                    className={styles.inputField}
                  />
                ) : (
                  <span className={styles.textField}>{product.price}</span>
                )}
              </td>
              <td>
                {isEditMode ? (
                  <input
                    type="text"
                    value={product.description}
                    onChange={(e) => handleInputChange(e, index, 'description')}
                    className={styles.inputField}
                  />
                ) : (
                  <span className={styles.textField}>{product.description}</span>
                )}
              </td>
              {isEditMode && (
                <td>
                  <button onClick={() => updateProduct(product.id)} className={styles.saveButton}>Save</button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>

      {isAddMode && (
        <div className={styles.modal}>
          <h3>Add Product</h3>
          <input
            type="text"
            name="name"
            value={newProduct.name}
            onChange={handleNewProductChange}
            placeholder="Product Name"
          />
          <input
            type="number"
            name="price"
            value={newProduct.price}
            onChange={handleNewProductChange}
            placeholder="Price"
          />
          <input
            type="text"
            name="description"
            value={newProduct.description}
            onChange={handleNewProductChange}
            placeholder="Description"
          />
          <button onClick={addNewProduct}>Save</button>
          <button onClick={() => setIsAddMode(false)}>Cancel</button>
        </div>
      )}

      {isDeleteMode && (
        <button onClick={deleteSelectedProducts} className={styles.confirmDelete}>
          Confirm Delete
        </button>
      )}
    </div>
  );
};

export default ManageProduct;
